import React from 'react';
import CollectionsOverviewComponent from '../../components/collections-overview/collections-overview.component';
import { Route } from 'react-router-dom';
import Collection from '../collection/collection.component';
import { connect } from 'react-redux';

import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions';
import { createStructuredSelector } from "reselect";
import { selectCollectionFetching } from "../../redux/shop/shop.selectos";

import  WithSpinner  from "../../components/with-spinner/with-spinner.component";

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverviewComponent)
const CollectionPageWithSpinner = WithSpinner (Collection);

class ShopPage extends React.Component {


  componentDidMount() {


  }

  render () {
    const { match, isCollectionFetching } = this.props;

    return (
      <div className="shop-page">
          <Route exact  path = {`${match.path}`} render={(props) => <CollectionsOverviewWithSpinner isLoding={isCollectionFetching} {...props} />} />
          <Route exact path= {`${match.path}/:collectionId`} render={(props) => <CollectionPageWithSpinner isLoding={isCollectionFetching} {...props} />}/>
      </div> 
    );
  }
}  

const mapStateToProps = createStructuredSelector({
  isCollectionFetching: selectCollectionFetching
})

const mapDispatchToProps = dispatch =>  ({
})

export default connect (mapStateToProps, mapDispatchToProps)(ShopPage);
